import React, { useState } from 'react'
import PropTypes from 'prop-types';
import { useParams, useHistory } from 'react-router-dom';


const UpdateProduct = ({ products, onUpdate }) => {
    let { id } = useParams();
    let history = useHistory();
    const product = products.find(product => product.id === id);

    const [currentProduct, setCurrentProduct] = useState(product);

    const onHandleSubmit = (e) => {
        e.preventDefault();
        onUpdate(currentProduct);
        history.push('/admin/products');
    }
    const onHandleChange = e => {
        const { name, value } = e.target;
        setCurrentProduct({
            ...currentProduct,
            [name]: value
        })
    }
    return (
        <div>
          <form action ="" onSubmit={handleSubmit(onHandleSubmit)}>
                    <div className="from-group">
                        <label htmlFor="InputProductName" > Product Name</label>
                        <input
                            type="text"
                            name="name"
                            className="form-control"
                            id="InputProductName"
                            ref={register({ required: true, minLength:5 })}
                        />
                        {errors.name && errors.name.type === "required" && <span>Product Name not null</span>}
                        {errors.name && errors.name.type == "minLength" && <span>Product Namme 5 word</span>}
                        <label htmlFor="InputProductPrice"> Price </label>
                        <input
                            type="text"
                            name="price"
                            className="form-control"
                            id="InputProductName"
                            ref={register({ required: true })}
                        />
                        {errors.name && <span> Price Not null </span>}
                        <label htmlFor="InputProductImage" > Image</label>
                        <input
                            type="text"
                            name="image"
                            className="form-control"
                            id="InputProductName"
                            ref={register({ required: true })}
                        />
                        {errors.name && <span>Image Not null</span>}
                    </div>
                    <button type="submit" className="btn btn-primary">Cập nhật</button>
                </form>
        </div>
    )
}

UpdateProduct.propTypes = {
    products: PropTypes.array
}

export default UpdateProduct
